import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { getProductCategory } from "../Actions/productActions";
import Loader from "../Layout/loader";
import Message from "../Layout/message";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import classes from "./FilterProduct.module.css";
import Rating from "./Rating/Rating";

function FilterProduct(props) {
  const dispatch = useDispatch();

  // FAIRING THE Action STATE
  const productListCategory = useSelector((state) => state.productCategory);
  const { loading, error, products } = productListCategory;

  // FAIRING THE Action STATE
  useEffect(() => {
    dispatch(getProductCategory());
  }, [dispatch]);

  // Filter By Categories
  function AccessoriesProduct(category) {
    const filterByAccessories = products.filter(
      (product) => product.category === category
    );
    // return filterByAccessories.slice(0, 8);
    return filterByAccessories;
  }

  function SamplePrevArrow(props) {
    const { className, style, onClick } = props;
    return (
      <div
        className={className}
        style={{
          ...style,
          display: "block",
          padding: "5px",
          backgroundColor: "rgb(250, 0, 75)",
          borderRadius: "40px",
          hover: "none",
          active: "none",
        }}
        onClick={onClick}
      />
    );
  }

  const settings = {
    dots: true,
    infinite: true,
    slidesToShow: 2,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 1000,
    pauseOnHover: true,
    nextArrow: <SamplePrevArrow />,
    prevArrow: <SamplePrevArrow />,
  };

  return (
    <div className={classes.container}>
      <h2>
        <span> {props.title} </span>
      </h2>
      <Slider {...settings}>
        {AccessoriesProduct(props.category).map((pro) => (
          <div>
            <div key={pro._id} className={classes.cart}>
              <div className={classes.card}>
                <button className={classes.discount1}> {pro.price}dh </button>
                <Link to={`/product/${pro._id}`} className={classes.link}>
                  <img src={pro.image} alt={pro.name} />
                </Link>
                <h className={classes.name}> {pro.name} </h>
                <p className={classes.price}> {pro.price}dh </p>
                <button className={classes.discount}> -25% </button>
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default FilterProduct;
