import React, { useEffect, useState } from "react";
import axios from "axios";
import { PayPalButton } from "react-paypal-button-v2";
import { useDispatch, useSelector } from "react-redux";
import classes from "./orders.module.css";
import { useParams } from "react-router-dom";
import Loader from "../Layout/loader";
import Message from "../Layout/message";
import {
  getOrderDetail,
  orderUpdatePaymentAction,
} from "../Actions/orderAction";
import { ORDER_DELIVER_RESET , ORDER_PAY_RESET } from "../Constants/orderConstant";
import Meta from "../Layout/Meta";

function Order() {
  const dispatch = useDispatch();
  const { id } = useParams();

  const [sdkIsReady, setSdkIsReady] = useState(false);

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const orderDetail = useSelector((state) => state.orderDetails);
  const { loading, error, order } = orderDetail;

  // Order Payment
  const orderPayment = useSelector((state) => state.orderUpdatePayment);
  const { loading: loadingPay, success: successPay } = orderDetail;

  const orderDeliver = useSelector((state) => state.orderToDeliver);
  const { success: successDeliver } = orderDeliver;

  useEffect(() => {
    const AddPaypalScript = async () => {
      const { data: ClientId } = await axios.get("/api/config/paypal");
      const script = document.createElement("script");
      script.type = "text/javascript";
      script.src = `https://www.paypal.com/sdk/js?client-id=${ClientId}`;
      script.async = true;
      script.onload = () => {
        setSdkIsReady(true);
      };

      document.body.appendChild(script);
    };

    // if (successDeliver) {
    //   dispatch({ type: ORDER_DELIVER_RESET });
    // }


    if(!order || successPay) { 
      dispatch({ type : ORDER_PAY_RESET });
      dispatch(getOrderDetail(id));
    } else if (!order.isPaid) {
      if (!window.paypal) {
        AddPaypalScript();
      } else {
        sdkIsReady(true);
      }
    }
  }, [dispatch, id, order , successPay]);

  // function OrderDeliveredHandler() {
  //   dispatch(orderDeliveryAction(order));
  // }

  function onSuccessPaymentHanlder (paymentResult)  {
    console.log(paymentResult);

    dispatch(orderUpdatePaymentAction(id, paymentResult))
  }

  return loading ? (
    <Loader />
  ) : error ? (
    <Message message={error} />
  ) : (
    <div className={classes.container}>
      {!order && (
        <Message
          message="no order"
          style={{ background: "red", color: "white" }}
        />
      )}
      <div className={classes.content}>
        <Meta title="shoppy-Games | Order Summary " />
        <div className={classes.card}>
          <h2> ORDER ID </h2>
          <p> {order._id} </p>
          <h3> User Info : </h3>
          <p> Name : {order.shippingAddress.name} </p>
          <p> Phone : {order.shippingAddress.phone} </p>
          <p> EMAIL : {userInfo.email} </p>
          <p>
            Address : {order.shippingAddress.address}-
            {order.shippingAddress.city} -{order.shippingAddress.postalCode}-
            {order.shippingAddress.country}
          </p>
          <h2> Total Amount : </h2>
          <p> {order.totalPrice} DH </p>
          <h2> ORDER PAYMENT STATUS : </h2>
             {order.isPaid &&  <button className={classes.btn1}> order is Paid </button>}
             {!order.isPaid &&  <button className={classes.btn}> Order Not Paid  </button>}
                <h3> ORDER Delivery STATUS : </h3>
             {order.isDelivered &&  <button className={classes.btn1}> order is Delivered </button>}
             {!order.isDelivered &&  <button className={classes.btn}> Order Not Delivered  </button>}
             <h3> SHOPPING ITEMS </h3>
          <div className={classes.items}>
            {order.orderItems.map((item, index) => (
              <div className={classes.item} key={index}>
                <div className={classes.img}>
                  <img
                    src={item.image}
                    alt={item.name}
                    className={classes.img}
                  />
                </div>
                <div> {item.name} </div>
                <div> qty : {item.qty} </div>
              </div>
            ))}
          </div>
        </div>
        <div className={classes.card2}>
          <h2 className={classes.orderSummary}> ORDER SUMMARY </h2>
          <p className={classes.amount}> Total Amount : <span classsName={classes.totalPrice}>{order.totalAmount}</span></p>
          <p className={classes.amount}> Shipping Price : 0$ - free sgippping </p>
          <p className={classes.amount}> Tax : 0$ </p> 
          <p className={classes.amount}> Amount To Pay : <span classsName={classes.totalPrice}>{order.totalAmount}</span></p>
          {!order.isPaid && (
            <div className={classes.pay}>
              {loadingPay && <Loader></Loader>}
              {!sdkIsReady ? <Loader /> : <PayPalButton amount={order.totalPrice} onSuccess={onSuccessPaymentHanlder} /> }
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Order;
